<?include 'includes/start.inc.php';checkLoggedIn();include 'includes/template.inc.php';template('Ship Design - Choose Hull', 'designChooseHullBody');function designChooseHullBody(){	global $eol, $mysqli, $lookups;	$query = $mysqli->prepare('SELECT hullid,hullname,hulldescription,metalcost,size,maxweapons FROM shiphulls ORDER BY maxweapons ASC, size ASC');	if (!$query)	{		echo 'error: ', $mysqli->error, $eol;		exit;	}	//$query->bind_param('i', $userid);	$result = $query->execute();	if (!$result)	{		echo 'error: ', $query->error, $eol;		exit;	}	$query->bind_result($hullid,$hullname,$hulldescription,$cost,$hullsize,$maxweapons);	echo '<form action="addshipdesign_form2.php" method="post">', $eol;	echo '<table>', $eol;	echo '<col><col><col><col><col><col style="width: 20em;">', $eol;	echo '<tr><th></th><th>Hull Name</th><th>Size</th><th>Cost</th><th>Max. Weapons</th><th>Description</th></tr>', $eol;	while($query->fetch())	{		echo '<tr><td><input type="radio" name="hull" value="',$hullid,'"></td><td>',$hullname,'</td><td>',$hullsize,'</td><td>',$cost,'</td><td>',$maxweapons,'</td><td>',$hulldescription,'</td></tr>', $eol;	}	echo '</table>', $eol;	echo '</form>', $eol;	$query->close();}?>