<?include_once 'includes/start.inc.php';checkLoggedIn();include_once 'includes/template.inc.php';template('Ship Design - Choose Hull', 'designChooseHullBody');function designChooseHullBody(){	global $eol, $mysqli, $lookups;	$query = $mysqli->prepare('SELECT hullid,hullname,hulldescription,metalcost,size,maxweapons,mindrydock FROM shiphulls ORDER BY maxweapons ASC, size ASC');	if (!$query)	{		echo 'error: ', $mysqli->error, $eol;		exit;	}	//$query->bind_param('i', $userid);	$result = $query->execute();	if (!$result)	{		echo 'error: ', $query->error, $eol;		exit;	}	$query->bind_result($hullid,$hullname,$hulldescription,$cost,$hullsize,$maxweapons,$mindrydock);	echo '<form action="addshipdesign_form2.php" method="get">', $eol;	echo '<table>', $eol;	echo '<col><col><col><col><col><col><col style="width: 20em;">', $eol;	echo '<tr><th></th><th>Hull Name</th><th>Size</th><th>Cost</th><th>Max. Weapons</th><th>Min. Drydock Level</th><th>Description</th></tr>', $eol;	$bFirst = true;	while($query->fetch())	{		echo '<tr><td>';		echo '<input type="radio" name="hull" value="',$hullid,'"';		if ($bFirst)		{			echo ' checked';			$bFirst = false;		}		echo '>';		echo '</td><td>',$hullname,'</td><td>',$hullsize,'</td><td>',$cost,'</td><td>',$maxweapons,'</td><td>',$mindrydock,'</td><td>',$hulldescription,'</td></tr>', $eol;	}	echo '</table>', $eol;	echo '<input type="submit" value="Next >">', $eol;	echo '</form>', $eol;	$query->close();}?>