<?include 'includes/start.inc.php';checkLoggedIn();include 'includes/template.inc.php';template('Create Ship Design', 'designCreateBody', null, 'designCreateHead');function designCreateHead(){?><script type="text/javascript">function minus(sectionid,min){	section = document.getElementById(sectionid);	remain = document.getElementById("remain");	if (section.value > min)	{		section.value -= 1;		remain.value = Number(remain.value) + 1;	}}function plus(sectionid,max){	section = document.getElementById(sectionid);	remain = document.getElementById("remain");	if (remain.value > 0 && (!max || section.value < max))	{		section.value = Number(section.value) + 1;		remain.value -= 1;	}}</script><?}function designCreateBody(){	global $eol, $mysqli, $lookups;	$hullid = $_POST['hull'];	$query = $mysqli->prepare('SELECT hullname,metalcost,size,maxweapons FROM shiphulls WHERE hullid=?');	if (!$query)	{		echo 'error: ', $mysqli->error, $eol;		exit;	}	$query->bind_param('i', $hullid);	$result = $query->execute();	if (!$result)	{		echo 'error: ', $query->error, $eol;		exit;	}	$query->bind_result($hullname,$cost,$hullsize,$maxweapons);	$result = $query->fetch();	if (!$result)	{		echo 'error: ', $query->error, $eol;		exit;	}	$query->close();	echo '<form action="addshipdesign_exec.php" method="post">', $eol;	echo '<table>', $eol;	echo '<tr><td>Hull:</td><td><input type="hidden" name="hull" value="',$hullid,'">',$hullname,'</td></tr>', $eol;	echo '<tr><td>Cost:</td><td>',$cost,' Metal</td></tr>', $eol;	echo '<tr><td>Size:</td><td>',$hullsize,'</td></tr>', $eol;	echo '<tr><td>Max Weapons:</td><td>',$maxweapons,'</td></tr>', $eol;	echo '<tr><td>Ship Name:</td><td><input type="text" size="14" name="shipname"></td></tr>', $eol;	echo '<tr><td>Engines:</td><td><input type="button" value="-" onclick="minus(\'engines\',1)"><input type="text" size="4" name="engines" id="engines" value="1"><input type="button" value="+" onclick="plus(\'engines\')"></td></tr>', $eol;	echo '<tr><td>Fuel bay:</td><td><input type="button" value="-" onclick="minus(\'fuel\',1)"><input type="text" size="4" name="fuel" id="fuel" value="1"><input type="button" value="+" onclick="plus(\'fuel\')"></td></tr>', $eol;	echo '<tr><td>Weapons:</td><td><input type="button" value="-" onclick="minus(\'weapons\',0)"><input type="text" size="4" name="weapons" id="weapons" value="0"><input type="button" value="+" onclick="plus(\'weapons\',',$maxweapons,')"></td></tr>', $eol;	echo '<tr><td>Shields:</td><td><input type="button" value="-" onclick="minus(\'shields\',0)"><input type="text" size="4" name="shields" id="shields" value="0"><input type="button" value="+" onclick="plus(\'shields\')"></td></tr>', $eol;	echo '<tr><td>Cargo:</td><td><input type="button" value="-" onclick="minus(\'cargo\',0)"><input type="text" size="4" name="cargo" id="cargo" value="0"><input type="button" value="+" onclick="plus(\'cargo\')"></td></tr>', $eol;	echo '<tr><td>Remaining:</td><td><input type="text" readonly size="4" id="remain" value="',$hullsize-2,'"></td></tr>', $eol;	echo '</table>', $eol;	echo '<input type="submit" value="Finish">', $eol;	echo '</form>', $eol;}?>