<?include 'includes/start.inc.php';checkLoggedIn();include 'includes/template.inc.php';template('Create Ship Design', 'designCreateBody');function designCreateBody(){	global $eol, $mysqli;	$names = array();	$stmt = $mysqli->prepare("SELECT * FROM shiphulls");	$stmt->execute();	$stmt->bind_result($hull,$hullname,$hulldesc,$cost,$size,$maxweap);?><script type="text/javascript">var names = new Array();var desc = new Array();var cost = new Array();var size = new Array();var weap = new Array();<?	while($stmt->fetch()){		$names[$hull]=$hullname;		//echo "names[" . $hull ."]=\"". $hullname ."\";\n";		echo "desc[" . $hull ."]=\"". str_replace('"','\"',$hulldesc) ."\";\n";		echo "cost[" . $hull ."]=\"". $cost ."\";\n";		echo "weap[" . $hull ."]=\"". $maxweap ."\";\n";		echo "size[" . $hull ."]=\"". $size ."\";\n";	}?></script><script type="text/javascript" src="functions.js"></script><form name="desform" action="addshipdesign_exec.php" method="post" onsubmit="return validateDesForm()"><select name="hull" id="hullselect" onChange="loadShip()"><?	foreach($names as $key => $value){		echo "<option value=\"" . $key ."\">" . $value . "</option>\n";	}?></select><br>Description: <span id="description"></span> <br>Cost: <span id="cost">0</span> Size: <span id="size">0</span> Max Weapons: <span id="maxweapons">0</span><br><br>Ship Name: <input type="text" name="shipname" id="shipname"><br>Engines: <input type="text" name="engines" id="engines" value=1> Fuel: <input type="text" name="fuel" id="fuel" value=1><br>Cargo: <input type="text" name="cargo" id="cargo" value="0"> Weapons: <input type="text" name="weapons" id="weapons" value=0> Shields: <input type="text" name="shields" id="shields" value=0> <br><input type="Submit" name="save" id="save" value="Save"></form><?}?><script type="text/javascript">loadShip();</script>