<?include_once 'includes/start.inc.php';include_once 'includes/template.inc.php';template('Register', 'registerBody');
function registerBody(){	global $eol, $mysqli;	$error = $_GET['error'];	$username = $_POST['username'];	if ($error)	{		echo '<p class="error">';		switch ($error)		{			case 1:				echo 'Username too short';			break;			case 2:				echo 'Username too long';			break;			case 3:				echo 'Password too short';			break;			case 4:				echo 'Username cannot contain spaces';			break;			case 10:				echo 'Username already taken';			break;		}		echo '</p>', $eol;	}
?><form action="register_exec.php" method="post"><input type="text" name="username" value="<?=htmlspecialchars($username)?>"><input type="password" name="password"><input type="submit" value="Submit"></form><?}?>